import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";
import "./App.css";
import axios from "axios";

function MancalaBoard() {
  const [computerPits, setComputerPits] = useState([4, 4, 4, 4, 4, 4]);
  const [humanPits, setHumanPits] = useState([4, 4, 4, 4, 4, 4]);
  const [computerStore, setComputerStore] = useState([0]);
  const [humanStore, setHumanStore] = useState([0]);
  const [currentPlayer, setCurrentPlayer] = useState(0);
  const { search } = useLocation();
  const params = new URLSearchParams(search);
  const name = params.get("name");
  const computerPlayer = params.get("computerPlayer");

  useEffect(() => {
    axios
      .get("http://localhost:8000/get_board")
      .then((response) => {
        const { board } = response.data;
        setComputerPits(board[0]);
        setHumanPits(board[2]);
        setComputerStore(board[1]);
        setHumanStore(board[3]);
      })
      .catch((error) => console.error("Error fetching board data:", error));
  }, []);

  const makeMove = (pitIndex) => {
    const moveData = {
      player_num: currentPlayer + 1,
      start_index: pitIndex,
    };

    axios
      .post("http://localhost:8000/make_move", moveData)
      .then((response) => {
        const { board, earned_free_move } = response.data;
        // setPits(board.slice(0, 2));
        // setStores(board.slice(2));
        if (earned_free_move) {
          console.log("Earned free move!");
        } else {
          setCurrentPlayer(1 - currentPlayer);
        }
      })
      .catch((error) => console.error("Error making move:", error));
  };

  const play = (pitIndex) => {
    let stones = computerPits[pitIndex]; //FIX_____________________________
    // if (stones === 0 || currentPlayer !== Math.floor(pitIndex / 6)) return;

    const newPits = computerPits.concat(humanPits);
    newPits[pitIndex] = 0;

    let currentIndex = pitIndex;
    while (stones > 0) {
      currentIndex = (currentIndex + 1) % 12;
      newPits[currentIndex]++;
      stones--;
    }

    if (
      newPits[currentIndex] === 1 &&
      Math.floor(currentIndex / 6) === currentPlayer
    ) {
      const oppositeIndex = 11 - currentIndex;
      const captured = newPits[oppositeIndex];
      newPits[oppositeIndex] = 0;
      const newStores = computerStore.concat(humanStore);
      newStores[currentPlayer] += captured + 1;
      newPits[currentIndex] = 0;
      setComputerStore(newStores); //FIXXXXXXXXXXX________________________
    }

    setComputerPits(newPits); //FIXXXXXXXXXXX________________________
    setCurrentPlayer(1 - currentPlayer);
    makeMove(pitIndex); // Make API call after local move
  };
  return (
    <div>
      <br></br>
      <h1>Welcome to Our Mancala Game, {name}!</h1>
      <br></br>
      <br></br>
      <div class="mancala-board">
        <div class="pits-row">
          {computerPits &&
            computerPits.reverse().map((stones, index) => (
              <div
                className="pit"
                key={5 - index}
                //onClick={() => play(index + 6)} ignore this
              >
                <div class="stones">{stones}</div>
              </div>
            ))}
        </div>
        <div class="stores-row">
          <div class="store-left">
            <div class="store-value">{computerStore}</div>
          </div>
          <div class="store-right"></div>
        </div>
        <div class="pits-row">
          {humanPits &&
            humanPits.map((stones, index) => (
              <div
                className="pit"
                key={index + 6}
                onClick={() => play(index + 6)}
              >
                <div class="stones">{stones}</div>
              </div>
            ))}
        </div>
      </div>
    </div>
  );
}

export default MancalaBoard;
