import React, { useState } from "react";
import { useLocation } from "react-router-dom";
import "./App.css";
import axios from "axios";

function MancalaBoard() {
  const [board, setBoard] = useState([]);
  const [freeMoveEarned, setFreeMoveEarned] = useState(false);

  const makeMove = async (playerNumber, moveIndex) => {
    try {
      const response = await axios.post("/make-move", {
        player_number: playerNumber,
        move_index: moveIndex,
      });
      const { board, free_move_earned } = response.data;
      setBoard(board);
      setFreeMoveEarned(free_move_earned);
    } catch (error) {
      console.error("Error making move:", error);
    }
  };

  // Render the game board and UI
  return (
    <div className="mancala-game">
      <div className="board">
        {/* Render the pits and stores */}
        {board.map((row, rowIndex) => (
          <div key={rowIndex} className="row">
            {row.map((stones, pitIndex) => (
              <div
                key={pitIndex}
                className={`pit ${stones === 0 ? "empty" : ""}`}
                onClick={() => makeMove(1, rowIndex)}
              >
                {stones}
              </div>
            ))}
          </div>
        ))}
      </div>
      <div className="status">
        {/* Render status messages, buttons, etc. */}
        <p>{freeMoveEarned ? "You earned a free move!" : "Make your move"}</p>
        <button onClick={() => makeMove(2, 0)}>Make Computer Move</button>
      </div>
    </div>
  );
}

export default MancalaBoard;
