import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";
import "./App.css";
import axios from "axios";

function MancalaBoard() {
  const [pits1, setPits1] = useState([4, 4, 4, 4, 4, 4]);
  const [pits2, setPits2] = useState([4, 4, 4, 4, 4, 4]);
  const [stores1, setStores1] = useState([0]);
  const [stores2, setStores2] = useState([0]);
  const [currentPlayer, setCurrentPlayer] = useState(0);
  const { search } = useLocation();
  const params = new URLSearchParams(search);
  const name = params.get("name");

  useEffect(() => {
    axios
      .get("http://localhost:8000/get_board")
      .then((response) => {
        const { board } = response.data;
        // Split the board into player pits and stores
        const player1Pits = board[0];
        const player2Pits = board[2]; // Adjusted slicing indices
        const player1Store = board[1];
        const player2Store = board[3]; // Adjusted slicing indices
        console.log(player1Pits);
        setPits1(player1Pits);
        setPits2(player2Pits);
        setStores1(player1Store);
        setStores2(player2Store);
      })
      .catch((error) => console.error("Error fetching board data:", error));
  }, []);

  const makeMove = (pitIndex) => {
    const moveData = {
      player_num: currentPlayer + 1,
      start_index: pitIndex,
    };

    axios
      .post("http://localhost:8000/make_move", moveData)
      .then((response) => {
        const { board, earned_free_move } = response.data;
        // setPits(board.slice(0, 2));
        // setStores(board.slice(2));
        if (earned_free_move) {
          console.log("Earned free move!");
        } else {
          setCurrentPlayer(1 - currentPlayer);
        }
      })
      .catch((error) => console.error("Error making move:", error));
  };

  const play = (pitIndex) => {
    let stones = pits1[pitIndex]; //FIX_____________________________
    // if (stones === 0 || currentPlayer !== Math.floor(pitIndex / 6)) return;

    const newPits = pits1.concat(pits2);
    newPits[pitIndex] = 0;

    let currentIndex = pitIndex;
    while (stones > 0) {
      currentIndex = (currentIndex + 1) % 12;
      newPits[currentIndex]++;
      stones--;
    }

    if (
      newPits[currentIndex] === 1 &&
      Math.floor(currentIndex / 6) === currentPlayer
    ) {
      const oppositeIndex = 11 - currentIndex;
      const captured = newPits[oppositeIndex];
      newPits[oppositeIndex] = 0;
      const newStores = stores1.concat(stores2);
      newStores[currentPlayer] += captured + 1;
      newPits[currentIndex] = 0;
      setStores1(newStores); //FIXXXXXXXXXXX________________________
    }

    setPits1(newPits); //FIXXXXXXXXXXX________________________
    setCurrentPlayer(1 - currentPlayer);
    makeMove(pitIndex); // Make API call after local move
  };
  //   console.log(pits1);
  //   console.log(pits2);
  //   return (
  //     <div>
  //       <h1>Welcome to Our Mancala Game, {name}!</h1>
  //       <div className="board">
  //         <div className="pits">
  //           <div className="store">{stores[1]}</div>
  //           {pits.slice(6).map((stones, index) => (
  //             <button
  //               className="stones"
  //               key={index + 6}
  //               onClick={() => play(index + 6)}
  //             >
  //               {stones}
  //             </button>
  //           ))}
  //           <div className="store">{stores[0]}</div>
  //         </div>
  //       </div>
  //       <div className="board">
  //         <div className="pits">
  //           {pits
  //             .slice(0, 6)
  //             .reverse()
  //             .map((stones, index) => (
  //               <button
  //                 className="stones"
  //                 key={5 - index}
  //                 onClick={() => play(5 - index)}
  //               >
  //                 {stones}
  //               </button>
  //             ))}
  //         </div>
  //       </div>
  //       <h3>Your Score: {stores[1]}</h3>
  //       <h3>Computer Score: {stores[0]}</h3>
  //     </div>
  //   );
  return (
    <div>
      <h1>Welcome to Our Mancala Game, {name}!</h1>
      <div className="board">
        <div className="pits">
          <div className="store">{stores1}</div>
          {/* {pits1 &&
            pits1.map((stones, index) => (
              <button
                className="stones"
                key={index + 6}
                onClick={() => play(index + 6)}
              >
                {stones}
              </button>
            ))} */}
          <div className="store">{stores2}</div>
        </div>
      </div>
      <div className="board">
        <div className="pits">
          {pits2 &&
            pits2.reverse().map((stones, index) => (
              <button
                className="stones"
                key={5 - index}
                onClick={() => play(5 - index)}
              >
                {stones}
              </button>
            ))}
        </div>
      </div>
      <h3>Your Score: {stores1}</h3>
      <h3>Computer Score: {stores2}</h3>
    </div>
  );
}

export default MancalaBoard;
