import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";
import "./App.css";
import axios from "axios";

function MancalaBoard() {
  const [computerPits, setComputerPits] = useState([4, 4, 4, 4, 4, 4]);
  const [humanPits, setHumanPits] = useState([4, 4, 4, 4, 4, 4]);
  const [computerStore, setComputerStore] = useState([0]);
  const [humanStore, setHumanStore] = useState([0]);
  const [currentPlayer, setCurrentPlayer] = useState(0);
  const [isComputersTurn, setIsComputersTurn] = useState(false);
  const { search } = useLocation();
  const params = new URLSearchParams(search);
  const name = params.get("name");
  const computerPlayer = params.get("computerPlayer");

  useEffect(() => {
    if (currentPlayer === 1 && computerPlayer === "Default") {
      setIsComputersTurn(true);
      setTimeout(() => {
        makeComputerMove();
      }, 1000); // Adjust the timeout as needed
    }
  }, [currentPlayer]); // Trigger this effect whenever currentPlayer changes

  const makeComputerMove = () => {
    // Implement logic to make a move for the computer player
    // For example, you can choose a random pit index
    const randomIndex = Math.floor(Math.random() * 6);
    makeMove(randomIndex);
    setIsComputersTurn(false);
  };

  const makeMove = (pitIndex) => {
    const moveData = {
      player_num: currentPlayer,
      start_index: pitIndex,
    };

    axios
      .post("http://localhost:8000/make_move", moveData)
      .then((response) => {
        const { board, earned_free_move } = response.data;
        setComputerPits(board[0]);
        setHumanPits(board[2]);
        setComputerStore(board[1]);
        setHumanStore(board[3]);
        if (earned_free_move) {
          console.log("Earned free move!");
        } else {
          setCurrentPlayer(1 - currentPlayer);
        }
      })
      .catch((error) => console.error("Error making move:", error));
  };

  return (
    <div>
      <br></br>
      <h1>Welcome to Our Mancala Game, {name}!</h1>
      <br></br>
      <br></br>
      <div class="mancala-board">
        {isComputersTurn && <div>Computer's Turn...</div>}{" "}
        {/* Display text indicating the computer's turn */}
        <div class="pits-row">
          {computerPits &&
            computerPits.reverse().map((stones, index) => (
              <div
                className="pit"
                key={5 - index}
                //onClick={() => play(index + 6)} ignore this
              >
                <div class="stones">{stones}</div>
              </div>
            ))}
        </div>
        <div class="stores-row">
          <div class="store-left">
            <div class="store-value">{computerStore}</div>
          </div>
          <div class="store-right">
            <div class="store-value">{humanStore}</div>
          </div>
        </div>
        <div class="pits-row">
          {humanPits &&
            humanPits.map((stones, index) => (
              <div className="pit" key={index} onClick={() => makeMove(index)}>
                <div class="stones">{stones}</div>
              </div>
            ))}
        </div>
      </div>
    </div>
  );
}

export default MancalaBoard;
